select.if:
			
			select * from 
			(select rownum as rnum,a.* from 
	        (select * from boardnotice  where bn_category=1 order by bn_no desc
	      	) a) 
	        where rnum>=#{startrow} and rnum<=#{endrow}

			
			select * from 
			(select rownum as rnum,a.* from 
	        (select * from boardnotice  where bn_category=2 order by bn_no desc
	      	) a) 
	        where rnum>=#{startrow} and rnum<=#{endrow}
